{
    "info": {
        "version": "12.5.2023",
        "id": "dfcTest",
        "title": "Depth Frequency Curve",
        "description": "Returns data for depth-frequency curve",
        "jobControlOptions": ["async-execute"],
        "outputTransmission": ["value"]
    },
    "host": {
        "type": "aws-batch",
        "jobDefinition": "process-sandbox:2",
        "jobQueue": "micro-test"
    },
    "command": ["python", "dfc.py"],
    "inputs": [
        {
            "id": "crs",
            "title": "crs",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 0,
            "maxOccurs": 1
        },
        {
            "id": "tile",
            "title": "tile",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 1
        },
        {
            "id": "epoch",
            "title": "epoch",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 1
        },
        {
            "id": "points",
            "title": "points",
            "description": "",
            "input": {
                "literalDataDomain": {
                    "dataType": "value",
                    "valueDefinition": {
                        "anyValue": true,
                        "possibleValues": null
                    }
                }
            },
            "minOccurs": 1,
            "maxOccurs": 999999
        }
    ],
    "outputs": [
        {
            "id": "dfc",
            "title": "dfc",
            "description": "",
            "output": {
                "transmissionMode": [
                    "value"
                ]
            }
        }
    ]
}

